SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";

CREATE TABLE `shiny_customer` (
  `id` int(11) NOT NULL,
  `type` enum('person','business') NOT NULL,
  `name` varchar(200) DEFAULT NULL,
  `phone` varchar(15) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `shiny_order` (
  `id` int(11) NOT NULL,
  `customer` int(11) DEFAULT NULL,
  `type` enum('patch','full') NOT NULL,
  `price` int(11) NOT NULL,
  `details` text NOT NULL,
  `task_start` datetime NOT NULL,
  `task_end` datetime DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `shiny_product` (
  `id` int(11) NOT NULL,
  `price` int(11) NOT NULL,
  `description` text NOT NULL,
  `active` int(11) NOT NULL DEFAULT '1'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `shiny_user` (
  `id` int(11) NOT NULL,
  `name` varchar(300) NOT NULL,
  `password` varchar(300) NOT NULL,
  `role` varchar(20) NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


